import React, {useState, useEffect} from 'react'
import axiosInstance from '../axios'
import { Navigate } from 'react-router-dom'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Modal from 'react-bootstrap/Modal';


export default function NewSession({type}) {


   

    const [formData, setformData]=useState()


    if ( type==='student') {
        return(
            <Navigate to ="/"/>
        )
        
    }
    
    let selectedStudents = [], student = [];

  
        
   fetchStudents()
   .then((list)=>{
 student = [...list]
   })

   function handleChange(e){
    setformData({
        ...formData,
        [e.target.name] : e.target.value.trim()
    })
   }




      

    

 

    
  return (
    <>

    <center>
        <h3 style={{marginTop: '1.5cm'}}>Initialize New Lab Session</h3><br/>
        <Form>

        <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Select date and time"
          type="datetime-local"
          name="dateTime"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          autoFocus
          
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Topic"
          name="topic"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Objective"
          name="objective"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Select file for Theory"
          type="file"
          name="theory"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      


        </Form>
    </center>

    </>
  )


async function fetchStudents(){
    const res= await axiosInstance.get('users/create')
    
    return res.data.filter(item => item.user_type==='student')

}


}
