import React, {useState, useEffect} from 'react'
import axiosInstance from '../axios'
import { Navigate } from 'react-router-dom'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Modal from 'react-bootstrap/Modal';


export default function NewSession({type}) {


   

    const [formData, setformData]=useState()
    const [show, setShow] = useState(false);
    const [suggestions, setsuggestions] = useState([])
    

    let selectedStudents = [], student = [];

  
useEffect(()=>{
  fetchStudents();
})

useEffect(()=>{
  console.log(suggestions)
}, [suggestions])
    


    if ( type==='student') {
        return(
            <Navigate to ="/"/>
        )
        
    }

    
    
    

    const handleShow = () => setShow(true);
  
        
   
  

   function handleChange(e){
    setformData({
        ...formData,
        [e.target.name] : e.target.value.trim()
    })
   }

   function handleClose(){
    setShow(false)
   }


   function handleInputChange(e){
    
    
      const input = e.target.value;
    
      const filteredStds = student.filter((one) =>{
        
       return ((one.firstname).toLowerCase()).startsWith(input.toLowerCase())}
    );
    
    setsuggestions(filteredStds)
    
    }

    function handleselect(e, id){
      
      
      if (e.target.checked){
    
    
     selectedStudents.push(id)
    console.log(selectedStudents)
    
   
    }
    
    else{
    selectedStudents = selectedStudents.filter((item)=>item!==id)
    console.log(selectedStudents)
    }
    
    }




      

    

 

    
  return (
    <>

    <center>
        <h3 style={{marginTop: '1.5cm'}}>Initialize New Lab Session</h3><br/>
        <Form>

        <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Select date and time"
          type="datetime-local"
          name="dateTime"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          autoFocus
          
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Topic"
          name="topic"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Objective"
          name="objective"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Select file for Theory"
          type="file"
          name="theory"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Requirements"
          name="requirements"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Resources"
          name="resources"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <InputGroup className="mb-1 bars" style={{width : '9cm', marginBottom: '0.5cm'}}>
        
        <Form.Control
          placeholder="Group name"
          name="sectionName"
          aria-describedby="basic-addon1"
          required
          onChange={handleChange}
          style={{marginBottom: '0.3cm'}}
        />
        
      </InputGroup>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Select students </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          
        <Form.Control
         autoFocus
          placeholder="Enter student's name"
          onChange={handleInputChange}
          id='sea'
          aria-describedby="basic-addon1"
          
        /><br/><br/>

        
         <>

         {            
         
         
         suggestions.map((stud)=>{
            <div key={stud.id}>
            <input type='checkbox' checked={ selectedStudents.includes(stud.id) }  onChange={(e)=>handleselect(e, stud.id)} style={{marginRight:'0.5cm'}}/> 
            <label> {stud.firstname} {stud.lastname} - RollNo : {stud.rollnum} </label>    

          <hr/><br/>
          </div>
          })
         }
          
         </>

         
        </Modal.Body>
        <Modal.Footer>
          
          <Button variant="dark" onClick={()=>handleClose()}>Done</Button>
        </Modal.Footer>
      </Modal>

      <Button variant="secondary" style={{marginRight:'0.5cm', width: '4cm'}} onClick={()=>{handleShow()}}>
        Select students
      </Button><br/><br/>

      <Button type="submit" variant="dark">Initialize</Button>


        </Form>
    </center>

    </>
  )


async function fetchStudents(){

  try{

  
  const res = await axiosInstance.get('users/create')
  if (student.length===0 && suggestions.length===0){
    student.push(...res.data.filter((item)=>item.user_type==='student'))
    setsuggestions([...res.data.filter((item)=>item.user_type==='student')])
  }
  


  }
  catch(err){
    console.log(err)
  }
  
    

  
    
    

}


}
