import React from 'react'
import { useParams } from 'react-router-dom'
import axiosInstance from '../axios';
import { useState, useEffect } from 'react';
import { getUser } from '../utils';

export default function EachView({type}) {
    const {id} = useParams();
    const [lab, setLab] = useState(null)
    const [instructor, setinstructor] = useState(null)

    

    useEffect(()=>{

        axiosInstance.get(`labify/oneLab/${id}`)
        .then((res)=>{
            setLab(res.data)
            
            getUser(res.data.instructor).then((n)=>setinstructor(n))
        })

        


    }, [id])

   
   

    if (lab!==null && instructor!==null && type!==null){

        return (

            <div style={{margin: '2cm'}}>
          
          <center>
              <h2>{lab.topic}</h2><br/>
          </center>
          <p hidden={instructor===null}><b>Instructor : </b>{instructor.firstname} {instructor.lastname}</p><br/>
   <p><b>Date-Time : </b>{lab.dateTime}</p><br/>
   <p><b>Objective : </b>{lab.objective}</p><br/>
   <p><b>Requirements : </b>{lab.requirements}</p><br/>
   <p><b>Theory : </b>{lab.theory}</p><br/>



          
            </div>
          
            )

    }
 
}
