import React from 'react'
import { useParams } from 'react-router-dom'
import axiosInstance from '../axios';
import { useState, useEffect } from 'react';
import { getUser } from '../utils';
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default function EachView({user}) {
    const {id} = useParams();
    const [lab, setLab] = useState(null)
    const [instructor, setinstructor] = useState(null)
    const [loading, setloading] = useState(true)
    const [studentLab, setstudentLab]=useState(null)
    const [pdf, setpdf]=useState(null)

    const handleFileChange = (e) => {
        setpdf(e.target.files[0]);
      };

useEffect(()=>{
    console.log(pdf)
}, [pdf])


      function handleSubmit(){
        if (pdf!==null){

        axios.post('http://127.0.0.1:8000/labify/upload', pdf)
        .then((res)=>{
            console.log(res)
        })
      }}

    useEffect(()=>{

        axiosInstance.get(`labify/oneLab/${id}`)
        .then((res)=>{
            setLab(res.data)
            setloading(false)
        })

        


    }, [id])

   if (loading){
    return(
        <h3>
            Loading...
        </h3>
    )
   }
   

    if (lab!==null  && user!==null){



        return (

            <div style={{margin: '2cm'}}>
          
          <center>
              <h2>{lab.topic}</h2><br/>
          </center>
          
   <p><b>Date-Time : </b>{lab.dateTime}</p><br/>
   <p><b>Objective : </b>{lab.objective}</p><br/>
   <p><b>Requirements : </b>{lab.requirements}</p><br/>
   <p><b>Theory : </b>{lab.theory}</p><br/>

<center>
<legend hidden={user.user_type==='instructor'}>Submit your initial</legend>
   <input hidden={user.user_type==='instructor'} type="file" onChange={handleFileChange}/><br/>
   <Button onClick={handleSubmit} variant="dark">Submit</Button>
   </center>




          
            </div>
          
            )

    }
 
}
